(window.webpackJsonp=window.webpackJsonp||[]).push([[35],{316:function(n,a,t){"use strict";t.r(a);var e=t(14),o=Object(e.a)({},(function(){var n=this,a=n._self._c;return a("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[a("h1",{attrs:{id:"舞蹈互动学习系统-互动研究工作流程和教学方法"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#舞蹈互动学习系统-互动研究工作流程和教学方法"}},[n._v("#")]),n._v(" 舞蹈互动学习系统：互动研究工作流程和教学方法")]),n._v(" "),a("p",[n._v("动作捕捉和全身交互技术已被实验证明有助于提高舞蹈水平\n学习和研究身体知识，同时创新舞蹈实践。设计与实施\n然而，旨在实现有效、愉快和有意义的教育体验的舞蹈互动学习系统是\n这是一个要求很高的跨学科且复杂的问题。在这项工作中，我们研究了交互式舞蹈训练系统\n在最近的参考书目中进行了描述，提出了最重要的设计参数的框架，我们将其与\n具体的实现示例。我们讨论公共工作流程的不同阶段的设计方式和\n在这些系统中实施，检查诸如向学习者反馈的可视化、涉及的运动质量等方面，\n使用的技术方法以及使用和学习方法的一般背景。我们的目标是找出共同点\n需要进一步研究和开发的模式和领域，以创造更有效和更有意义的数字舞蹈\n学习工具")]),n._v(" "),a("h1",{attrs:{id:"引言"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#引言"}},[n._v("#")]),n._v(" 引言")]),n._v(" "),a("p",[n._v("根据最近的科学文献和各种研究工作的记录，在过去十年中，数字环境和动作捕捉技术已通过不同的视角应用于舞蹈实践领域，例如艺术创作和实验、研究、教育等。具体来说，在舞蹈教育领域，最近的技术进步导致了“通过不同的技术（如交互式全身体验、动作捕捉、虚拟现实和增强现实）设计和开发支持舞蹈教学和学习的工具” [33]。这些工具试图增强和支持舞蹈学习和教学，以及在某些情况下创新对动作本身的分析和反思。尽管“使用交互式技术支持舞蹈学习和教育仍然是一个相对未开发的领域” [32]，但可以肯定的是，它可以为掌握表现力动作的领域做出重大贡献。这项贡献包括研究身体知识、创新舞蹈教学、保护文化遗产、革新编舞、拓宽舞蹈的渠道和实践以及增强表演体验。此外，该领域应用的技术可以扩展并应用于运动起着核心作用的其他领域，例如体育、康复、表演和艺术创作等。考虑到这一研究背景以及当前相关研究项目，如 WhoLoDancE EU 项目 [75]，这项工作试图概述专门为舞蹈教育目的设计的交互式舞蹈学习系统的现有方法。这些方法根据拟议的框架进行分类，该框架可以告知该领域当前的概念和技术差距并指导后续研究计划")]),n._v(" "),a("p",[n._v("除了纯粹的舞蹈教育系统外，还有其他几种方法通过各种交互（从基于网络和桌面到舞台应用程序）支持舞蹈创作、分析和研究等各种背景下的舞蹈实践。其中一些工具，如由 Calvert 等人引入并由 Wilke 等人扩展的 Laban Dancer [11] 和 Life Forms [13]，标志着计算机科学与 Merce Cunningham 等编舞家之间的对话开始，引入了在 3D 化身上生成动作的工具并在编舞中使用计算机，正如 Thecla Schiphorst [65] 所报告和调查的那样。最近，许多跨学科项目探索了使用技术进行动作分析和创作。这些例子包括 Motion Bank [41] 和 Choreographic Objects [42]，它们是编舞家 William Forsythe、舞蹈学者、交互设计师和计算机研究人员合作的成果，Moving Stories [66] 是一个跨学科的合作项目和研究小组，专注于技术设计中的动作，以及“TKB，表演艺术的跨媒体知识库” [39]，它研究了编舞、语言学和计算机科学的交叉点。此外，欧盟资助的项目，如 i-treasures [48]、Dance EU [29] 和 WhoLoDanceE [75]，从教育和创意的角度研究和应用捕捉和分析舞蹈动作的技术，以及保护非物质文化遗产的工具。上述努力产生了许多系统和工具，它们在体验类型（例如基于网络、桌面、AR、VR）、背景和目的方面各不相同。更具体地说，这些成果包括用于动作分析的注释工具，如 Singh 等人的 Choreographer’s notebook [67]、Delahunta 等人在 Motion Bank 项目 [31] 框架下开发的 Piecemaker，以及专注于基于本体的芭蕾舞注释的 BalOnSe [34][35]。类似的工具包括用于协作目的和远程协同工作的在线应用程序，如 Fernandes 等人提出的 BlackBox [40]。")]),n._v(" "),a("p",[n._v("此外，增强表演设置和交互技术已被设计和开发，以在表演和场景制作的背景下创建增强和虚拟空间，例如 Instituto Stocos1、K. Danse2 和类似的舞蹈艺术公司的作品，它们还开发了将动作及其品质转化为各种表演模式的系统，如 Palacio 和 Bisig [60] 的作品。所有这些应用都与舞蹈学习系统和环境存在一些相似之处或重叠之处，它们直接为运动分析提供信息，包括概念建模、表示、多模态可视化和转换。因此，对它们进行了研究，以形成这项工作的框架，但由于我们的重点是纯粹的全身互动教育系统，因此不会详细介绍它们。另一类与舞蹈相关的应用是商业游戏，例如“舞蹈革命” [28][46]、“舞蹈中心聚光灯” [76] 和“舞力全开”[49] 电子游戏系列。尽管这些应用与学习环境有许多相似之处，并吸引了大量受众，但它们的目的和使用环境是娱乐而不是教育。更具体地说，在这些情况下，正如 Chan 等人 [22] 所指出的，“输入数据被大量减少以方便分析动作，尽管这种减少在娱乐环境中是可以接受的，但系统无法为全身运动提供足够的反馈以用于训练目的”。根据 Charbonnau 等人 [24] 提出的结果，舞蹈游戏似乎对提高舞蹈表演和学习没有多大帮助，因为通常反馈只是一个评判总分，但这并不能表明哪里出了问题，也不能表明改进。考虑到这一点，舞蹈游戏被列为表 1 中的交互式环境类别，但没有特别关注它们采用的交互类型和反馈。")])])}),[],!1,null,null,null);a.default=o.exports}}]);